def nbre_journaliste(ar)
    "le nombre de journaliste est: " + ar.length.to_s
end

def hundle_avec_num(ar)
    a = 0
    ar.each{ |x|
        if (x =~ /[0-9]/) != nil
            #puts x
            a += 1
        end
    }
    puts "il y #{a} hundle avec des nombres"
end

def hundle_avec_aude(ar)
    b = 0
    ar.each{ |x|
        y = x.downcase
        if y.match('aude') != nil
            #puts y
            b += 1
        end
    }
    puts "il y a #{b} hundle comportant le mot 'aude' non sensible a la case"
end

def hundle_commencant_par_maj(ar)
    c = 0
    ar.each{|x|
        z = x.gsub(/[^A-Za-z0-9 ]/, '')
        if z[0].upcase == z[0]
            #puts x
            c += 1
        end
    }
    puts "il y a #{c} hundle commencant par un majuscul"
end

def hundle_contenant_maj(ar)
    d = 0
    ar.each{|x|
        t = x.gsub(/[^A-Z ]/, '')
        if t != ""
            #puts x
            d += 1
        end
    }
    puts "il y a #{d} hundle contenant un majuscul"
end

def pseudo_confondu(ar)
    e = 0
    ar.each{|x|
        u = x.gsub(/[^_ ]/, '')
        if u != ''
            #puts "#{x} ==============> a #{u.length} underscore"
            e += 1
        end
    }
    puts "il y #{e} de pseudo avec un '_'"
end

def ordre_alphabetique(ar)
    arr = ar.sort{|x, y|
        x <=> y
    }

    arr.each{|z|
        #puts z
    }

end


